import './App.css';
import axios from 'axios';
import React, { useState, useEffect } from 'react';

const AddShoppingForm = ({ onAdd }) => {
  const [title, setTitle] = useState('');
  const [quantity, setQuantity] = useState('');

  const handleSubmit = e => {
    e.preventDefault();
    axios.post('http://localhost:3032/Buy/add', {
      title,
      quantity
    })
      .then(res => {
        onAdd(res.data);
        setTitle('');
        setQuantity('');
      })
      .catch(err => {
        console.error(err);
      });
  };
  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="title">Title:</label>
      <input
        type="text"
        id="title"
        value={title}
        onChange={e => setTitle(e.target.value)}
      />

      <label htmlFor="quantity">Quantity</label>
      <textarea
        id="quantity"
        value={quantity}
        onChange={e => setQuantity(e.target.value)}
      />


      <button type="submit">Add to your list</button>
    </form>
    );
  };

const List = () => {
  const [list, setlist] = useState([]);
  const fetchlist = () => {
    axios.get('http://localhost:8081/buy/')
      .then(res => {
        console.log(res);
        setlist(res.data);
      });
  };

  useEffect(() => {
    fetchlist();
  }, []);
  return list.map((list, index) => {
    return (
      <div key={index}>
        <h1>{list.title}</h1>
        <h1>{list.quantity}</h1>
      </div>
    )
  })
};

function App() {
  const [list, setlist] = useState([]);
  const handleAddItem = list=> {
    setlist([...list, list]);
  };

  return (
    <div className="App">
      <AddShoppingForm onAdd={handleAddItem} />
      <list />
    </div>
  );
}

export default App;
